import React from "react";
import gql from "graphql-tag";
import { useMutation, useQuery } from "@apollo/react-hooks";


const OFFER_QUERY = gql`
  {
    allOffres {
        data {
        title
        address {
            ville
        }
        description
        postedBy {
            email
            id_firebase_auth
            _id
        }
        }
    }
  }
`;

const DELETE_ITEM = gql`
  mutation DeleteItem($id: ID!) {
    deleteItem(id: $id) {
      _id
    }
  }
`;

const UPDATE_ITEM = gql`
  mutation UpdateItem($id: ID!, $isComplete: Boolean!) {
    updateItem(id: $id, data: { isComplete: $isComplete }) {
      _id
      isComplete
    }
  }
`;

export default function OfferList() {
  const { data, loading } = useQuery(OFFER_QUERY);
/*
  const [deleteItem, { loading: deleteLoading }] = useMutation(DELETE_ITEM, {
    refetchQueries: [{ query: ITEMS_QUERY }],
  });

  const [updateItem, { loading: updateLoading }] = useMutation(UPDATE_ITEM);
*/
  if (loading) {
    return <div class="d-flex justify-content-center">
                <div class="spinner-grow text-dark" role="status">
                <span class="sr-only">Loading...</span>
                </div>
            </div>
    ;
  }

  return (
      <div class="card-deck">
        {data.allOffres.data.map((item) => {
            
            return (
                    <div class="card " >
                        <div class="card-body">
                            <h5 class="card-title">{item.title}</h5>
                            <p class="card-text">{item.description}</p>
                        </div>
                        <ul class="list-group list-group-flush">
                            <li class="list-group-item">+212 6 42 68 79 80</li>
                            <li class="list-group-item">lot. Adarissa, Sidi Maarouf, Grand Casablanca</li>
                        </ul>
                        <div class="card-body">
                            <a href="#" class="card-link">Response to this offer</a>
                        </div>
                        <div class="card-footer">
                            <small class="text-muted">Posted by {item. postedBy.email}</small>
                        </div>
                    </div>
            );
        })}
    </div>
  );
}
