import React from "react";
import gql from "graphql-tag";
import { useMutation, useQuery } from "@apollo/react-hooks";

const OFFER_QUERY = gql`
  {
    allOffres {
        data {
        title
        address {
            ville
        }
        description
        postedBy {
            email
            id_firebase_auth
            _id
        }
        }
    }
  }
`;

const DELETE_ITEM = gql`
  mutation DeleteItem($id: ID!) {
    deleteItem(id: $id) {
      _id
    }
  }
`;

const UPDATE_ITEM = gql`
  mutation UpdateItem($id: ID!, $isComplete: Boolean!) {
    updateItem(id: $id, data: { isComplete: $isComplete }) {
      _id
      isComplete
    }
  }
`;

export default function OfferList() {
  const { data, loading } = useQuery(OFFER_QUERY);
/*
  const [deleteItem, { loading: deleteLoading }] = useMutation(DELETE_ITEM, {
    refetchQueries: [{ query: ITEMS_QUERY }],
  });

  const [updateItem, { loading: updateLoading }] = useMutation(UPDATE_ITEM);
*/
  if (loading) {
    return <div>Loading...</div>;
  }

  return (
      <div>
        <ul>
        {data.allOffres.data.map((item) => {
            const labelStyles = item.isComplete
            ? { style: { textDecoration: "line-through" } }
            : {};
            return (
            <li >
                <span {...labelStyles}>{item.title}</span>
                <span {...labelStyles}> Posted by {item. postedBy.email}</span>
                <span {...labelStyles}>{item.description}</span>
            </li>
            );
        })}
        </ul>

        <div class="card mb-3">
            <img class="card-img-top" src="..." alt="Card image cap"/>
            <div class="card-body">
                <h5 class="card-title">Card title</h5>
                <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
            </div>
        </div>
    </div>
  );
}
