import {React, Component} from 'react';
import { Field, reduxForm } from 'redux-form'
import TextField from 'material-ui/TextField'
import { RadioButton, RadioButtonGroup } from 'material-ui/RadioButton'
import Checkbox from 'material-ui/Checkbox'
import SelectField from 'material-ui/SelectField'
import MenuItem from 'material-ui/MenuItem'


class Prestation extends Component {
    static getInitialProps({store}) {}

    constructor(props) {
      super(props);
      this.renderTextField = this.renderTextField.bind(this);
      this.renderCheckbox = this.renderCheckbox.bind(this);
      this.renderRadioGroup = this.renderRadioGroup.bind(this);
      this.renderSelectField = this.renderSelectField.bind(this);
    }
  
      renderTextField = ({ input, label, meta: { touched, error }, ...custom }) => (
      <TextField hintText={label}
          floatingLabelText={label}
          errorText={touched && error}
          {...input}
          {...custom}
      />
      )
  
      renderCheckbox = ({ input, label }) => (
      <Checkbox label={label}
          checked={input.value ? true : false}
          onCheck={input.onChange}/>
      )
  
      renderRadioGroup = ({ input, ...rest }) => (
      <RadioButtonGroup {...input} {...rest}
          valueSelected={input.value}
          onChange={(event, value) => input.onChange(value)}/>
      )
  
      renderSelectField = ({ input, label, meta: { touched, error }, children, ...custom }) => (
      <SelectField
          floatingLabelText={label}
          errorText={touched && error}
          {...input}
          onChange={(event, index, value) => input.onChange(value)}
          children={children}
          {...custom}/>
      )

    render() {
      return (
        <form >
          <div>
            <Field name="firstName" component={this.props.renderTextField} label="First Name"/>
          </div>
          <div>
            <Field name="lastName" component={this.props.renderTextField} label="Last Name"/>
          </div>
          <div>
            <Field name="email" component={this.props.renderTextField} label="Email"/>
          </div>
          <div>
            <Field name="sex" component={this.props.renderRadioGroup}>
              <RadioButton value="male" label="male"/>
              <RadioButton value="female" label="female"/>
            </Field>
          </div>
          <div>
            <Field name="favoriteColor" component={this.props.renderSelectField} label="Favorite Color">
              <MenuItem value={'ff0000'} primaryText="Red"/>
              <MenuItem value={'00ff00'} primaryText="Green"/>
              <MenuItem value={'0000ff'} primaryText="Blue"/>
            </Field>
          </div>
          <div>
            <Field name="employed" component={this.props.renderCheckbox} label="Employed"/>
          </div>
          <div>
            <Field name="notes" component={this.props.renderTextField} label="Notes" multiLine={true} rows={2}/>
          </div>
          <div>
            <button type="submit" disabled={pristine || submitting}>Submit</button>
            <button type="button" disabled={pristine || submitting} onClick={reset}>Clear Values
            </button>
          </div>
        </form>
      )
    }
  }

  validate = values => {
    const errors = {}
    const requiredFields = [ 'firstName', 'lastName', 'email', 'favoriteColor', 'notes' ]
    requiredFields.forEach(field => {
        if (!values[ field ]) {
        errors[ field ] = 'Required'
        }
    })
    if (values.email && !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
        errors.email = 'Invalid email address'
    }
    return errors
    }

    export default reduxForm({
      form: 'MaterialUiForm',  // a unique identifier for this form
      validate
    })(Prestation);
